mapper.update:
update inform
set content = #{content,jdbcType=VARCHAR},
  object = #{object,jdbcType=VARCHAR},
  way = #{way,jdbcType=VARCHAR},
  time = #{time,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, content, object, way, time
from inform
where id = #{id,jdbcType=INTEGER}
